import "./App.css";

import UserList from "./component/UserList";
import UserData from "./component/UserData";

import { useState } from "react";
import { color } from "@mui/system";
// console.log(UserData[0].name);
function App() {
  const [inHover, setHover] = useState(false);

  return (
    <div className="main">
      <table>
        <tr>
          <th className="settitle1">Name</th>
          <th className="settitle2">Status</th>
          <th className="settitle3"> Access</th>
        </tr>
      </table>
      <div className="main2">
        {inHover && (
          <p className="hover">
            {" "}
            <div class="card">
              <center>
              <img
                src={inHover.image.props.src}
                alt="Avatar"
                className="imgset"
              />
              <div class="container">
                <h4>
                  <b >{inHover.name}</b>
                </h4>
                <p className="cardname">{inHover.email}</p>
              </div>
              <button className="cardbtn"><b>Active User</b></button><br/>

              <p className="progbar">plan Uses</p>
              <progress value="2450" max="5000"></progress>
              <h3>2450</h3>
              </center>
            </div>
          </p>
        )}
        {UserData.map((user, index) => {
          return (
            <UserList
              key={index}
              setHover={setHover}
              imgavtar={user.imgavtar}
              name={user.name}
              email={user.email}
              status={user.status}
              Access={user.Access}
              action={user.action}
            />
          );
        })}
      </div>
    </div>
  );
}

export default App;
